const menuItems = [
  {
    id: '1',
    name: 'X-Salada',
    description: 'Pão, hambúrguer, queijo, alface e tomate.',
    price: 'R$ 25,00',
    image: 'data:image/jpeg;base64,', // Substitua pela URL da imagem do X-Salada
  },
  {
    id: '2',
    name: 'X-Bacon',
    description: 'Pão, hambúrguer, queijo, bacon, alface e tomate.',
    price: 'R$ 28,00',
    image: 'data:image/jpeg;base64,', // Substitua pela URL da imagem do X-Bacon
  },
  {
    id: '3',
    name: 'Batata Frita',
    description: 'Porção grande de batata frita com sal.',
    price: 'R$ 15,00',
    image: 'data:image/jpeg;base64,', // Substitua pela URL da imagem da Batata Frita
  },
  {
    id: '4',
    name: 'Refrigerante',
    description: 'Lata de 350ml.',
    price: 'R$ 8,00',
    image: 'data:image/jpeg;base64,', // Substitua pela URL da imagem do Refrigerante
  },
  {
    id: '5',
    name: 'Milkshake de Chocolate',
    description: 'Milkshake cremoso de chocolate.',
    price: 'R$ 18,00',
    image: 'data:image/jpeg;base64,', // Substitua pela URL da imagem dos Anéis de Cebola
  },
];

const MenuItem = ({ item }) => {
  const handleAddToOrder = () => {
    Alert.alert(`Item adicionado: ${item.name}`);
  };

  return (
    <View style={styles.menuItem}>
      <Image style={styles.itemImage} source={{ uri: item.image }} />
      <View style={styles.itemDetails}>
        <Text style={styles.itemName}>{item.name}</Text>
        <Text style={styles.itemDescription}>{item.description}</Text>
        <Text style={styles.itemPrice}>{item.price}</Text>
        <Button title="Adicionar ao Pedido" onPress={handleAddToOrder} />
      </View>
    </View>
  );
};

export default function App() {
  return (
    <ScrollView style={styles.container}>
      <Text style={styles.header}>Cardápio da Lanchonete</Text>
      {menuItems.map(item => (
        <MenuItem key={item.id} item={item} />
      ))}
    </ScrollView>
  );
}

